    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        int sc=Integer.parseInt(tf1.getText());
        int e=Integer.parseInt(tf2.getText());
        int m=Integer.parseInt(tf3.getText());
        int h=Integer.parseInt(tf4.getText());
        int sst=Integer.parseInt(tf5.getText());
        int t=sc+e+m+h+sst;
        JOptionPane.showMessageDialog(null,t,"Your Total marks are:",JOptionPane.INFORMATION_MESSAGE);
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        // TODO add your handling code here:
        float sc=Integer.parseInt(tf1.getText());
        float e=Integer.parseInt(tf2.getText());
        float m=Integer.parseInt(tf3.getText());
        float h=Integer.parseInt(tf4.getText());
        int sst=Integer.parseInt(tf5.getText());
        float t=sc+e+m+h+sst;
         float p=(t/500)*100;
         if(p>=94)
        JOptionPane.showMessageDialog(null,p,"Grade is" +"A",JOptionPane.INFORMATION_MESSAGE);
         else if(p>=85)
        JOptionPane.showMessageDialog(null,p,"Grade is" +"A",JOptionPane.INFORMATION_MESSAGE);
        else if(p>=78)
        JOptionPane.showMessageDialog(null,p,"Grade is" +"B+",JOptionPane.INFORMATION_MESSAGE);
        else if(p>=68)
        JOptionPane.showMessageDialog(null,p,"Grade is" +"B",JOptionPane.INFORMATION_MESSAGE);
       else if(p>=60)
        JOptionPane.showMessageDialog(null,p,"Grade is" +"C+",JOptionPane.INFORMATION_MESSAGE);
        else if(p>=52)
        JOptionPane.showMessageDialog(null,p,"Grade is" +"C",JOptionPane.INFORMATION_MESSAGE);
        else if(p>=45)
        JOptionPane.showMessageDialog(null,p,"Grade is" +"D+",JOptionPane.INFORMATION_MESSAGE);
        else if(p>=33)
        JOptionPane.showMessageDialog(null,p,"Grade is" +"D",JOptionPane.INFORMATION_MESSAGE);
        else
        JOptionPane.showMessageDialog(null,p,"FAIL",JOptionPane.INFORMATION_MESSAGE);
    }//GEN-LAST:event_jButton2ActionPerformed

